package com.oracle.api;

import javax.ws.rs.Consumes;
import javax.ws.rs.GET;
import javax.ws.rs.POST;
import javax.ws.rs.Path;
import javax.ws.rs.PathParam;
import javax.ws.rs.Produces;
import javax.ws.rs.core.MediaType;
import javax.ws.rs.core.Response;

import com.oracle.exception.GeneralException;
import com.oracle.service.bank.BankService;
import com.oracle.service.customer.CustomerService;
import com.oracle.service.loanapplication.LoanApplicationService;
import com.oracle.service.repayment.RepaymentService;

@Path("")
public class ApplicationInterface {

	@GET
	@Path("/verifyUser/{customerId}")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response userExist(@PathParam("customerId") String customerId) {
		try {
			System.out.println(customerId);
			CustomerService customerService = new CustomerService();
			return customerService.userExist(customerId);
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}


	@POST
	@Path("/addLoanApplication")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response addLoanApplication(String json) {
		try {
			System.out.println(json);
			LoanApplicationService loanApplicationService = new LoanApplicationService();
			return loanApplicationService.addLoanApplication(json);
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}


	@GET
	@Path("/getLoanApplication/{applicationId}")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response getLoanApplicationById(@PathParam("applicationId") String applicationId) {
		try {
			LoanApplicationService loanApplicationService = new LoanApplicationService();
			return loanApplicationService.getLoanApplication(applicationId);
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}

	@GET
	@Path("/getLoanApplication/date")
	@Produces(MediaType.APPLICATION_JSON)
	public Response getLoanApplicationDateWise() {
		try {
			LoanApplicationService loanApplicationService = new LoanApplicationService();
			return loanApplicationService.getAllLoanApplicationDateWise();
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}


	@GET
	@Path("/getLoanApplication/status")
	@Produces(MediaType.APPLICATION_JSON)
	public Response getLoanApplicationStatusWise() {
		try {
			LoanApplicationService loanApplicationService = new LoanApplicationService();
			return loanApplicationService.getAllLoanApplicationStatusWise();
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}


	@GET
	@Path("/depositMoney/{applicationId}")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response getBankDeposit(@PathParam("applicationId") String applicationId) {
		try {
			BankService bankService = new BankService();
			Response response = bankService.creditAmount(applicationId);
			return response;
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}


	@POST
	@Path("/addCustomer")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response addCustomer(String jsonCustomerData) {
		try {
			CustomerService customerService = new CustomerService();
			return customerService.addNewCustomer(jsonCustomerData);
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}

	@GET
	@Path("/getCustomerCID/{customerId}")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response getCustomerFromCustomerId(@PathParam("customerId") String customerId) {
		try {
			CustomerService customerService = new CustomerService();
			return customerService.getCustomerByCustomerId(customerId);
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}


	@GET
	@Path("/getCustomerAID/{applicationId}")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response getCustomerFromApplicationId(@PathParam("applicationId") String applicationId) {
		try {
			CustomerService customerService = new CustomerService();
			return customerService.getCustomerByApplicationId(applicationId);
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}


	@POST
	@Path("/repayLoan")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response repaymentForLoan(String jsonRepaymentData) {
		try {
			RepaymentService repaymentService = new RepaymentService();
			return repaymentService.repayLoanAmount(jsonRepaymentData);
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}

	@GET
	@Path("imageData")
	@Produces(MediaType.APPLICATION_JSON)
	public Response sendImage() {
		String jsonString = "{\"value\":\"data:image/jpeg;base64,\"}";
		return Response.status(200).entity(jsonString).build();
	}


	@POST
	@Path("/approveLoan")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response approveLoan(String json) {
		System.out.println(json);
		LoanApplicationService loanApplicationService = new LoanApplicationService();
		return loanApplicationService.updateStatus(json);
	}


	@POST
	@Path("/addCustomerComplete")
	@Consumes(MediaType.APPLICATION_JSON)
	@Produces(MediaType.APPLICATION_JSON)
	public Response addCustomerFullDetail(String json) {
		try {
		System.out.println(json);
		CustomerService customerService = new CustomerService();
		return customerService.addNewCustomer(json);
		} catch (Exception e) {
			throw new GeneralException(e.getMessage());
		}
	}

}